import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  margin-bottom: 20px;
`;

const ColorGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
  gap: 10px;
  width: 100%;
`;

const ColorSwatch = styled.div`
  height: 150px;
  background-color: ${(props) => props.color};
  display: flex;
  align-items: flex-end;
  justify-content: center;
  color: #fff;
  font-weight: bold;
  padding: 10px;
`;

const Dropdown = styled.select`
  padding: 5px;
  margin-right: 10px;
`;

const Button = styled.button`
  padding: 10px 20px;
  margin: 0 5px;
`;

export default function CreatePalette() {
  const [colorFormat, setColorFormat] = useState('hex');
  const [colors, setColors] = useState([
    '#1ABC9C',
    '#16A085',
    '#9B59B6',
    '#E74C3C',
    '#2C3E50',
  ]);

  const handleColorFormatChange = (e) => {
    setColorFormat(e.target.value);
  };

  const addNewColor = () => {
    // Function to add a new color to the palette
  };

  const goBack = () => {
    // Function to navigate back to the previous page
  };

  return (
    <Container>
      <Header>

        <Button onClick={goBack}>Back</Button>
        <div>
        <Dropdown onChange={handleColorFormatChange} value={colorFormat}>
          <option value="hex">HEX</option>
          <option value="rgb">RGB</option>
          <option value="hsl">HSL</option>
        </Dropdown>
          <Button onClick={addNewColor}>Add Color</Button>
        </div>
      </Header>
      <ColorGrid>
        {colors.map((color, index) => (
          <ColorSwatch key={index} color={color}>
            {color.toUpperCase()}
          </ColorSwatch>
        ))}
      </ColorGrid>
    </Container>
  );
}
