import React, { useState, useEffect } from 'react';
import '../MyColors.css';
import { EditMyColors } from './EditMyColors';
import { apiRequest } from './api';


function MyColors() {


    const [colors, setColors] = useState(() => {
        const storedColors = localStorage.getItem('colors');
        return storedColors ? JSON.parse(storedColors) : [];
    });

    const [editingColor, setEditingColor] = useState(null);
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {

        fetchColors();
    }, []);



    const fetchColors = async () => {
        const userData = JSON.parse(localStorage.getItem('userData'));
        const user_id = userData.userId;

        const endpoint = `/users/${user_id}/colors`; // URLSearchParams may not be needed if user_id is in the path
        try {
            const response = await apiRequest(endpoint, 'GET');
            if (response && response.colors) {
                const colorsArray = response.colors.map(color => {

                    return { hex: color };


                });

                setColors(colorsArray);

                localStorage.setItem('colors', JSON.stringify(colorsArray));

                console.log('Colors saved to localStorage:', localStorage.getItem('colors'));
            }
        } catch (error) {
            console.error('Error fetching colors:', error);
        }
    };








    const copyToClipboard = (hex) => {
        navigator.clipboard.writeText(hex).then(() => {
            alert(`Copied ${hex} to clipboard`); 
        }).catch(err => {
            console.error('Could not copy text: ', err);
        });
    };

    const handleColorUpdate = (newColor, index) => {
        let updatedColors = [...colors];
        if (index !== -1) {
            updatedColors[index] = {...updatedColors[index], hex: newColor};
            setColors(updatedColors);
            localStorage.setItem('colors', JSON.stringify(updatedColors));
        }
    };

    const removeColor = async(indexToRemove) => {
        console.log(colors[indexToRemove]);


        const userData = JSON.parse(localStorage.getItem('userData'));

        const user_id = userData.userId;
        const token = userData.token;
        const color = colors[indexToRemove];


        const queryParams = new URLSearchParams({user_id, color: color, token });
        const endpoint = `/users/${user_id}/colors?${queryParams}`;


        try {
            const response = await apiRequest(endpoint, 'POST');
            if (response) {
              setShowNiceMessage(true);
              setTimeout(() => setShowNiceMessage(false), 2000);
            }
          } catch (error) {
            console.error('Error adding color:', error);
          }



        localStorage.setItem('colors', JSON.stringify(newColors));
        setColors(newColors);
    };

    const editColor = (color) => {
        setEditingColor(color); // Store the hex value of the color being edited
        setIsOpen(true); // Open the modal
    };



    return (
        <div className="myColorsContainer">
            <h1>My Colors</h1>
            <div className="colorsGrid">
                {colors.map((color, index) => (
                    <div key={index} className="colorItem">
                        <div
                            className="colorSquare"
                            style={{ backgroundColor: color.hex }}
                            onClick={() => copyToClipboard(color.hex)}
                        ></div>
                        <div className="colorHex">{color.hex}</div>

                        <div className="editIcon" onClick={() => editColor(color)}> </div>


                        <EditMyColors
                            isOpen={isOpen}
                            onClose={() => setIsOpen(false)}
                            initialColor={editingColor ? editingColor.hex : '#FFFFFF'} 
                            onColorUpdate={(newColor) => handleColorUpdate(newColor, colors.indexOf(editingColor))}
                        />

                        <div className="deleteIcon" onClick={() => removeColor(index)}>
                            &times;
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default MyColors;
