import React, { useState } from 'react';

export default function Search() {
  // State to hold search results
  const [searchResults, setSearchResults] = useState([]);
  // State to hold the current search input
  const [searchQuery, setSearchQuery] = useState('');

  // Function to call the search API
  const fetchSearchResults = async () => {
    const url = `http://localhost:8000/users/search?username=${searchQuery}`;
    try {
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        },
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setSearchResults(data); // assuming the API returns an array of results
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  // Function to handle changes to the input field
  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Function to handle form submission
  const handleSearchSubmit = (event) => {
    event.preventDefault(); // Prevent default form submission behavior
    fetchSearchResults();
  };

  return (
    <div>

      <form onSubmit={handleSearchSubmit}>
        <input
          type="text"
          value={searchQuery}
          onChange={handleInputChange}
          placeholder="Search by username"
        />
        <button type="submit">Search</button>
      </form>
      <ul>
        {searchResults.map((result, index) => (
          <li key={index}>{result.username || 'No username provided'}</li> // adjust according to the data structure
        ))}
      </ul>
    </div>
  );
}
