import React, { useState } from 'react';
import { apiRequest } from './api'; // Ensure this module exists and is properly defined

const Login = ({ onLoginSuccess }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (username, password) => {
        try {
            // Use the apiRequest function to make the API call
            const data = await apiRequest('/token', 'POST', {
                username: username,
                password: password,
            });

            // Call the onLoginSuccess callback or handle the login success scenario
            onLoginSuccess(data);
        } catch (error) {
            // Handle errors, for example, show an error message
            setError(error.message || 'An error occurred during login.');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!username || !password) {
            setError('Both fields are required');
            return;
        }
        handleLogin(username, password);
    };

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit}>
                <h2>Login</h2>
                {error && <p className="error">{error}</p>}
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
