import React, { useState } from 'react';

export default function Search() {
  // State to hold search results
  const [searchResults, setSearchResults] = useState([]);

  // Function to call the search API
  const fetchSearchResults = async (query) => {
    const url = `http://localhost:8000/users/search?username=${query}`;
    try {
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        },
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setSearchResults(data); // assuming the API returns an array of results
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  // Example usage (could be triggered by a search input field, for example)
  // Uncomment the next line to fetch results based on a specific query, like 'o'
  // useEffect(() => {
  //   fetchSearchResults('o');
  // }, []);

  return (
    <div>
      <h1>Search Results</h1>
      <ul>
        {searchResults.map((result, index) => (
          <li key={index}>{result.username}</li> // adjust according to the data structure
        ))}
      </ul>
    </div>
  );
}
