import React, { useState, useEffect, useCallback } from "react";
import { ChromePicker } from 'react-color';
import { ReactComponent as IconClose } from "./icon-close.svg";
import "./EditMyColors.css";

export const EditMyColors = ({ isOpen, onClose, initialColor }) => {
  const [currentColor, setCurrentColor] = useState(initialColor);
  const [tempColor, setTempColor] = useState(initialColor); // Temporarily hold color changes

  // Update currentColor when initialColor changes
  useEffect(() => {
    setCurrentColor(initialColor);
    setTempColor(initialColor); // Reset temp color on initial color change
  }, [initialColor]);

  const handleColorChange = useCallback((color) => {
    setTempColor(color.hex); // Store color changes temporarily
  }, []);

  const handleSaveColor = () => {
    // Check for color existence in localStorage
    let existingColors = JSON.parse(localStorage.getItem('colors')) || [];
    if (existingColors.includes(tempColor)) {
      alert("This color already exists.");
    } else {
      existingColors.push(tempColor);
      localStorage.setItem('colors', JSON.stringify(existingColors));
      setCurrentColor(tempColor); // Update the current color with the new selection
      onClose(); // Close the modal after saving
    }
  };

  const handleCancel = () => {
    setTempColor(currentColor); // Revert any unsaved changes
    onClose(); // Close the modal
  };

  if (!isOpen) return null;

  return (
    <div className="modal-wrapper" onClick={onWrapperClick}>
      <div className="modal-content" style={{ textAlign: 'center' }}>
        <button className="modal-close-button" onClick={onClose}>
          <IconClose />
        </button>
        <ChromePicker
          color={tempColor}
          onChangeComplete={handleColorChange}
        />
        <div>
          <button onClick={handleSaveColor} style={{ marginRight: '10px' }}>Save</button>
          <button onClick={handleCancel}>Cancel</button>
        </div>
      </div>
    </div>
  );
};

const onWrapperClick = (event) => {
  if (event.target.classList.contains("modal-wrapper")) onClose();
};
