import React, { useState, useEffect } from "react";
import { ReactComponent as IconClose } from "./icon-close.svg";
import "./EditMyColors.css";

export const EditMyColors = ({ isOpen, onClose, hexColor }) => {
  if (!isOpen) return null;

  // Utility functions to convert HEX to HSL and HSL to HEX would go here
  // For brevity, they are not included in this snippet

  // Convert the initial HEX color to its HSL representation
  const [hsl, setHsl] = useState(hexToHsl(hexColor)); // This function needs to be defined
  const [hex, setHex] = useState(hexColor);

  useEffect(() => {
    setHex(hslToHex(hsl)); // This function needs to be defined
  }, [hsl]);

  const onWrapperClick = (event) => {
    if (event.target.classList.contains("modal-wrapper")) onClose();
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setHsl({ ...hsl, [name]: value });
  };

  return (
    <div className="modal-wrapper" onClick={onWrapperClick}>
      <div className="modal-content">
        <div className="modal-header">
          <button className="modal-close-button" onClick={onClose}>
            <IconClose />
          </button>
        </div>
        <div className="color-preview" style={{ backgroundColor: hex }} />
        <div>
          <label>
            Hue:
            <input
              type="range"
              name="h"
              min="0"
              max="360"
              value={hsl.h}
              onChange={handleChange}
            />
          </label>
          <label>
            Saturation:
            <input
              type="range"
              name="s"
              min="0"
              max="100"
              value={hsl.s}
              onChange={handleChange}
            />
          </label>
          <label>
            Lightness:
            <input
              type="range"
              name="l"
              min="0"
              max="100"
              value={hsl.l}
              onChange={handleChange}
            />
          </label>
        </div>
        {hex}
      </div>
    </div>
  );
};
