import React, { useState } from 'react';
import { BASE_URL } from '../constants';

const Search = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [users, setUsers] = useState([]);

    const handleSearch = async () => {
        try {
            const response = await fetch(`${BASE_URL}/users/search/?username=${searchTerm}`, {
                method: 'GET',
                headers: {
                    'accept': 'application/json',
                }
            });

            console.log(response);

        } catch (error) {
            console.error('Error fetching users:', error);
        }

    };

    return (
        <div>
            <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder="Search users"
            />
            <button onClick={handleSearch}>Search</button>
            <div>
                {users.length > 0 ? (
                    <ul>
                        {users.map((user) => (
                            <li key={user.id}>
                                <p>Username: {user.username}</p>
                                <p>Colors: {user.colors?.join(', ') || 'None'}</p>
                                <p>Palettes: {Object.entries(user.palettes).length > 0
                                    ? Object.entries(user.palettes).map(([name, colors]) => (
                                        <div key={name}>
                                            <strong>{name}</strong>: {colors.join(', ')}
                                        </div>
                                    ))
                                    : 'None'
                                }</p>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No users found.</p>
                )}
            </div>
        </div>
    );
};

export default Search;
