import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const SearchContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
  max-width: 600px;
  margin: auto;
  padding: 20px;
  box-sizing: border-box;
`;

const SearchInput = styled.input`
  margin-bottom: 10px;
  width: 100%;
  max-width: 300px;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
`;

const SearchButton = styled.button`
  width: 100%;
  max-width: 300px;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #5cb85c;
  color: white;
  cursor: pointer;
`;

const SearchTitle = styled.h1`
  /* Add styles for your search title here if needed */
`;

const SearchResultList = styled.ul`
  list-style: none;
  /* Add styles for your search result list here if needed */
`;

const SearchResultItem = styled.li`
  cursor: pointer;
  /* Add styles for your search result items here if needed */
`;

export default function Search() {
  const [searchResults, setSearchResults] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const fetchSearchResults = async () => {
    const url = `http://localhost:8000/users/search?username=${searchQuery}`;
    try {
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Accept': 'application/json',
        },
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      setSearchResults(data); 
      console.log(data); 
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    fetchSearchResults();
  };

  const handleResultClick = (username, userId) => {
    console.log('Clicked username:', username, 'with ID:', userId); 
    if (userId) {
      navigate(`/my-profile/${userId}`); 
    } else {
      console.error('User ID is undefined'); 
    }
  };

  return (
    <div className="search-container">
      <h1 className="search-title">Search</h1>
      <form onSubmit={handleSearchSubmit} className="search-form">
        <input
          type="text"
          className="search-input"
          value={searchQuery}
          onChange={handleInputChange}
          placeholder="Search by username"
        />
        <button type="submit" className="search-button">Search</button>
      </form>
      <ul className="search-results">
        {searchResults.map((result, index) => (
          <li key={index} onClick={() => handleResultClick(result.username, result.id)} className="search-result-item">
            {result.username || 'No username provided'}
          </li>
        ))}
      </ul>
    </div>
  );
}
