import React, { useState, useEffect } from "react";
import { PhotoshopPicker } from 'react-color';
import { ReactComponent as IconClose } from "./icon-close.svg";
import "./EditMyColors.css";

export const EditMyColors = ({ isOpen, onClose, initialColor, onColorUpdate }) => {
  const [currentColor, setCurrentColor] = useState(initialColor); // Temporary local state

  useEffect(() => {
    setPickerKey(Math.random());  // Reset the key when initialColor changes
}, [initialColor]);



  useEffect(() => {
    setCurrentColor(initialColor); // Update the local state when the initial color changes
  }, [initialColor]);

  const handleColorChange = (color) => {
    setCurrentColor(color.hex); // Update only the local state
  };

  const handleSave = () => {
    if (onColorUpdate) {
      onColorUpdate(currentColor); // Call the update function only on save
    }
    onClose(); // Close the modal after saving
  };

  const handleCancel = () => {
    onClose(); // Close the modal without saving
  };

  if (!isOpen) return null;

  return (
    <div className="modal-wrapper" onClick={(event) => {
      if (event.target.classList.contains("modal-wrapper")) onClose();
    }}>
      <div className="modal-content" style={{ textAlign: 'center' }}>
        <button className="modal-close-button" onClick={onClose}>
          <IconClose />
        </button>
        <PhotoshopPicker
          key={picker}
          color={currentColor}
          onChangeComplete={handleColorChange}
          onAccept={handleSave}
          onCancel={handleCancel}
        />


      </div>
    </div>
  );
};
