import React, { useState } from 'react';
import ''

function MyColors() {
    const [colors, setColors] = useState(() => {
        const storedColors = localStorage.getItem('colors');
        return storedColors ? JSON.parse(storedColors) : [];
    });

    // Function to copy the HEX color to the clipboard
    const copyToClipboard = (hex) => {
        navigator.clipboard.writeText(hex).then(() => {
            alert(`Copied ${hex} to clipboard`);
        }).catch(err => {
            console.error('Could not copy text: ', err);
        });
    };

    // Function to remove a color from the list
    const removeColor = (indexToRemove) => {
        const newColors = colors.filter((_, index) => index !== indexToRemove);
        localStorage.setItem('colors', JSON.stringify(newColors));
        setColors(newColors);
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px' }}>
            <h1>My Colors</h1>
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                {colors.map((color, index) => (
                    <div key={index} style={{ position: 'relative', margin: '10px' }}>
                        <div style={{
                            width: '100px',
                            height: '100px',
                            backgroundColor: color.hex,
                            marginBottom: '5px',
                            cursor: 'pointer'
                        }}
                        onClick={() => copyToClipboard(color.hex)}>
                        </div>
                        <div style={{ textAlign: 'center' }}>{color.hex}</div>
                        <div style={{
                            position: 'absolute',
                            top: 0,
                            right: 0,
                            cursor: 'pointer',
                            color: 'white',
                            background: 'red',
                            borderRadius: '50%',
                            width: '20px',
                            height: '20px',
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            fontSize: '16px',
                            lineHeight: '20px',
                            userSelect: 'none'
                        }}
                        onClick={() => removeColor(index)}>&times;</div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default MyColors;
