import React from "react";
import { ReactComponent as IconClose } from "./icon-close.svg";
import {AlphaPicker, HuePicker} from "react-color";
import {useState, useCallback} from "react";
import "./EditMyColors.css";




export const EditMyColors = ({ isOpen, onClose, children }) => {

  const [currentColor, setCurrentColor] = useState({children})

  if (!isOpen) return null; 

  const onWrapperClick = (event) => {
    if (event.target.classList.contains("modal-wrapper")) onClose();
  };

  const pickerContainerStyle = {
    marginTop: '20px',
    display: 'flex',

    justifyContent: 'center',
  };

  const appStyle = {
    backgroundColor: `rgba(${currentColor.r}, ${currentColor.g}, ${currentColor.b}, ${currentColor.a})`,
    height: "100vh",
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: "center",
  };

  return (
    <div className="modal-wrapper" onClick={onWrapperClick}>
      <div className="modal-content">
        <button className="modal-close-button" onClick={onClose}>
          <IconClose />
        </button>

        <div style = {appStyle}>


        </div>
        <div style={pickerContainerStyle}>
          <h3> Hue Picker</h3>
          <HuePicker/>
          </div>

        <div style={pickerContainerStyle}>
          <h3> Alpha Picker</h3>
          <AlphaPicker/>
          </div>


        {children}
      </div>
    </div>
  );
};
