import { useState, useCallback } from 'react';
import { ChromePicker } from 'react-color';
import _ from 'lodash';

function App() {
  const [currentColor, setCurrentColor] = useState({
    r: 255,
    g: 255,
    b: 102,
    a: 1
  });
  const [showNiceMessage, setShowNiceMessage] = useState(false);

  styleButton = {
    backgroundColor: `rgba(${currentColor.r}, ${currentColor.g}, ${currentColor.b}, ${currentColor.a})`,
    color: 'white',
    padding: '10px',
    border: 'none',
    borderRadius: '5px',

  }


  const handleColorChange = useCallback(_.throttle((color) => {
    setCurrentColor(color.rgb);
  }, 100), []); 

  const handleAddColor = () => {
    setShowNiceMessage(true);
    setTimeout(() => {
      setCurrentColor({ r: 255, g: 255, b: 255, a: 1 }); // Reset to white
      setShowNiceMessage(false); // Hide message
    }, 2000); // 2 seconds before resetting
  };

  const appStyle = {
    backgroundColor: `rgba(${currentColor.r}, ${currentColor.g}, ${currentColor.b}, ${currentColor.a})`,
    height: "100vh",
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: "center",
  };



  return (
    <div className="App" style={appStyle}>
      <div>
        <h1>Color Palette</h1>
        <ChromePicker 
          color={currentColor}
          onChange={handleColorChange}
        />
        <button onClick={handleAddColor} style={styleButton}>Add Color</button>
        {showNiceMessage && <p>NICE</p>}
      </div>
    </div>
  );
}

export default App;
