import React from 'react';
import { CustomPicker } from 'react-color';
import { Hue, Saturation } from 'react-color/lib/components/common';
import { ReactComponent as IconClose } from "./icon-close.svg";
import "./EditMyColors.css";

// Create a custom Saturation slider
const MySaturation = ({ hsl, onChange }) => {
  return (
    <div style={{ position: 'relative', width: '100%', height: '150px' }}>
      <Saturation hsl={hsl} onChange={onChange} />
    </div>
  );
};

// Create a custom Hue slider
const MyHue = ({ hue, onChange }) => {
  return (
    <div style={{ position: 'relative', width: '100%', height: '10px' }}>
      <Hue hue={hue} onChange={onChange} direction="horizontal" />
    </div>
  );
};

// You'll need to create a custom Lightness component using a similar pattern
// or another method because 'react-color' does not provide a Lightness component directly.

const MyColorPicker = (props) => {
  return (
    <div>
      <MySaturation {...props} />
      <MyHue {...props} />
      {/* Implement a lightness component */}
    </div>
  );
};

const CustomColorPicker = CustomPicker(MyColorPicker);

export const EditMyColors = ({ isOpen, onClose, color }) => {
  if (!isOpen) return null;

  const onWrapperClick = (event) => {
    if (event.target.classList.contains("modal-wrapper")) onClose();
  };

  return (
    <div className="modal-wrapper" onClick={onWrapperClick}>
      <div className="modal-content">
        <button className="modal-close-button" onClick={onClose}>
          <IconClose />
        </button>
        <CustomColorPicker color={color} />
      </div>
    </div>
  );
};
