import { useState, useCallback } from 'react';
import { SketchPicker, HuePicker, AlphaPicker, Saturation } from 'react-color';
import _ from 'lodash';

function App() {
  // Convert RGB to HSL
  const rgbToHsl = (r, g, b) => {
    r /= 255, g /= 255, b /= 255;
    let max = Math.max(r, g, b), min = Math.min(r, g, b);
    let h, s, l = (max + min) / 2;

    if (max === min) {
      h = s = 0; // achromatic
    } else {
      let d = max - min;
      s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
      switch (max) {
        case r: h = (g - b) / d + (g < b ? 6 : 0); break;
        case g: h = (b - r) / d + 2; break;
        case b: h = (r - g) / d + 4; break;
      }
      h /= 6;
    }
    return { h: Math.round(h * 360), s: Math.round(s * 100), l: Math.round(l * 100) };
  };

  const [color, setColor] = useState({
    rgb: { r: 255, g: 255, b: 102, a: 1 },
    hsl: rgbToHsl(255, 255, 102)
  });

  const handleColorChange = useCallback(_.throttle((color) => {
    setColor({
      rgb: color.rgb,
      hsl: color.hsl || rgbToHsl(color.rgb.r, color.rgb.g, color.rgb.b) // Fallback to conversion if no HSL provided
    });
  }, 100), []); 

  const appStyle = {
    backgroundColor: `rgba(${color.rgb.r}, ${color.rgb.g}, ${color.rgb.b}, ${color.rgb.a})`,
    height: "100vh",
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: "center",
  };

  const pickerContainerStyle = {
    marginTop: '20px',
  };

  return (
    <div className="App" style={appStyle}>
      <div>
        <h1>Color Palette</h1>
        <SketchPicker 
          color={color.rgb}
          onChange={handleColorChange}
        />
        <div style={pickerContainerStyle}>
          <HuePicker
            color={color.hsl}
            onChange={handleColorChange}
            width="90%"
          />
        </div>
        <div style={pickerContainerStyle}>
          <AlphaPicker
            color={color.hsl}
            onChange={handleColorChange}
            width="90%"
          />
        </div>
        <div style={pickerContainerStyle}>
          <Saturation
            color={color.hsl}
            onChange={handleColorChange}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
