import { useState, useCallback } from 'react';
import { SketchPicker, HuePicker, AlphaPicker } from 'react-color';
import { rgbToHsl, hslToRgb } from './colorUtils'; // Utility functions you may need to implement
import _ from 'lodash';

function App() {
  const [currentColor, setCurrentColor] = useState({
    r: 255, g: 255, b: 102, a: 1,
    h: 60, s: 50, l: 70 
  });

  const handleColorChange = useCallback(_.throttle((color) => {
    const {h, s, l} = rgbToHsl(color.rgb.r, color.rgb.g, color.rgb.b);
    setCurrentColor({ ...color.rgb, h, s, l });
  }, 100), []);

  const handleHueChange = useCallback(_.throttle((color) => {
    setCurrentColor(current => ({
      ...current,
      h: color.h
    }));
  }, 100), []);

  const handleSaturationChange = useCallback(_.throttle((value) => {
    setCurrentColor(current => {
      const {r, g, b} = hslToRgb(current.h, value, current.l);
      return { ...current, s: value, r, g, b };
    });
  }, 100), []);

  const handleLightnessChange = useCallback(_.throttle((value) => {
    setCurrentColor(current => {
      const {r, g, b} = hslToRgb(current.h, current.s, value);
      return { ...current, l: value, r, g, b };
    });
  }, 100), []);

  const appStyle = {
    backgroundColor: `rgba(${currentColor.r}, ${currentColor.g}, ${currentColor.b}, ${currentColor.a})`,
    height: "100vh",
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: "center",
  };

  const pickerContainerStyle = {
    marginTop: '20px',
  };

  return (
    <div className="App" style={appStyle}>
      <div>
        <h1>Color Palette</h1>
        <SketchPicker 
          color={currentColor}
          onChange={handleColorChange}
        />
        <div style={pickerContainerStyle}>
          <h2>Hue Picker</h2>
          <HuePicker
            color={currentColor}
            onChange={handleHueChange}
            width="90%"
          />
        </div>
        <div style={pickerContainerStyle}>
          <h2>Saturation Picker</h2>
          <input type="range" min="0" max="100" value={currentColor.s}
            onChange={e => handleSaturationChange(parseFloat(e.target.value))}
          />
        </div>
        <div style={pickerContainerStyle}>
          <h2>Lightness Picker</h2>
          <input type="range" min="0" max="100" value={currentColor.l}
            onChange={e => handleLightnessChange(parseFloat(e.target.value))}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
