import React, { useState } from 'react';

export default function CreatePalette() {
  const [paletteName, setPaletteName] = useState('');
  const [selectedColors, setSelectedColors] = useState([]);

  const history = useNavigate();

  const goBack = () => {
    history.push('/my-palettes'); // Change the URL to '/my-palettes', or the appropriate path
  };

  // Function to handle adding a new color to the selectedColors array
  const addColor = (color) => {
    setSelectedColors([...selectedColors, color]);
  };

  // Function to handle removing a color from the selectedColors array
  const removeColor = (colorIndex) => {
    setSelectedColors(selectedColors.filter((_, index) => index !== colorIndex));
  };

  // Function to handle palette name change
  const handlePaletteNameChange = (event) => {
    setPaletteName(event.target.value);
  };

  // Function to handle saving the palette
  const savePalette = () => {
    // Save palette logic here
    // e.g., send to backend or save to local storage
    console.log('Palette Saved:', paletteName, selectedColors);
  };

  return (
    <div className='create-palette-container'>
      <h2>Create a New Color Palette</h2>
      <input
        type="text"
        value={paletteName}
        onChange={handlePaletteNameChange}
        placeholder="Enter palette name"
      />
      <div className='selected-colors-container'>
        {selectedColors.map((color, index) => (
          <div key={index} style={{ backgroundColor: color }}>
            <span>{color}</span>
            <button onClick={() => removeColor(index)}>Remove</button>
          </div>
        ))}
      </div>
      <button onClick={addColor}>Add Color</button> {/* This button should trigger a color picker */}
      <button onClick={savePalette}>Save Palette</button>
    </div>
  );
}
