import './App.css';
import { SketchPicker, HuePicker, AlphaPicker } from 'react-color';
import { useState } from 'react';

function App() {
  // Use an object to store color, including RGB, alpha, and hex
  const [currentColor, setCurrentColor] = useState({
    r: 255,
    g: 255,
    b: 102,
    a: 1
  });

  const handleColorChange = (color) => {
    setCurrentColor(color.rgb); // Update the color using the RGB and alpha from SketchPicker
  };

  const handleHueChange = (color) => {
    setCurrentColor(current => ({
      ...current,
      r: color.rgb.r,
      g: color.rgb.g,
      b: color.rgb.b
    })); // Only change the RGB, keep the alpha the same
  };

  const handleAlphaChange = (color) => {
    setCurrentColor(current => ({
      ...current,
      a: color.rgb.a
    })); // Only update the alpha
  };

  const appStyle = {
    backgroundColor: `rgba(${currentColor.r}, ${currentColor.g}, ${currentColor.b}, ${currentColor.a})`,
    height: "100vh",
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: "center",
  };

  const pickerContainerStyle = {
    marginTop: '20px', // Add margin at the top of the picker container
  };

  return (
    <div className="App" style={appStyle}>
      <div>
        <h1>Color Palette</h1>
        <SketchPicker 
          color={currentColor}
          onChangeComplete={handleColorChange}
        />
        <div style={pickerContainerStyle}>
          <h2>Hue Picker</h2>
          <HuePicker
            color={currentColor}
            onChangeComplete={handleHueChange}
            width="90%"
          />
        </div>
        <div style={pickerContainerStyle}>
          <h2>Alpha Picker</h2>
          <AlphaPicker
            color={currentColor}
            onChangeComplete={handleAlphaChange}
            width="90%"
          />
        </div>
      </div>
    </div>
  );
}

export default App;
