import { useState, useCallback } from 'react';
import { SketchPicker, HuePicker, AlphaPicker, Saturation } from 'react-color';
import _ from 'lodash';



function App() {

  // Utility function to convert RGB to HSL
function rgbToHsl(r, g, b) {
  r /= 255, g /= 255, b /= 255;
  var max = Math.max(r, g, b), min = Math.min(r, g, b);
  var h, s, l = (max + min) / 2;

  if (max === min) {
    h = s = 0; // achromatic
  } else {
    var d = max - min;
    s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
    switch (max) {
      case r: h = (g - b) / d + (g < b ? 6 : 0); break;
      case g: h = (b - r) / d + 2; break;
      case b: h = (r - g) / d + 4; break;
    }
    h /= 6;
  }

  return { h: h * 360, s: s, l: l };
}

// Include this function in your component or as a separate utility module



  const [color, setColor] = useState({
    r: 255, g: 255, b: 102, a: 1,
    hsl: rgbToHsl(255, 255, 102)  // Initialize HSL values here
  });




  const handleColorChange = useCallback(_.throttle((color) => {
    setColor({
      rgb: color.rgb,
      hsl: color.hsl // react-color provides hsl values too, if not, convert from rgb
    });
  }, 100), []); 

  const appStyle = {
    backgroundColor: `rgba(${color.rgb.r}, ${color.rgb.g}, ${color.rgb.b}, ${color.rgb.a})`,
    height: "100vh",
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: "center",
  };

  const pickerContainerStyle = {
    marginTop: '20px',
  };

  return (
    <div className="App" style={appStyle}>
      <div>
        <h1>Color Palette</h1>
        <SketchPicker 
          color={color.rgb}
          onChange={handleColorChange}
        />
        <div style={pickerContainerStyle}>
          <HuePicker
            color={color.hsl}
            onChange={handleColorChange}
            width="90%"
          />
        </div>
        <div style={pickerContainerStyle}>
          <AlphaPicker
            color={color.hsl}
            onChange={handleColorChange}
            width="90%"
          />
        </div>
        <div style={pickerContainerStyle}>
          <Saturation
            color={color.hsl}
            onChange={handleColorChange}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
