import { useState, useCallback } from 'react';
import { ChromePicker } from 'react-color';
import _ from 'lodash';

function App() {
  const [currentColor, setCurrentColor] = useState({
    r: 255,
    g: 255,
    b: 102,
    a: 1
  });
  const [showNiceMessage, setShowNiceMessage] = useState(false);

  const handleColorChange = useCallback(_.throttle((color) => {
    setCurrentColor(color.rgb);
  }, 100), []); 

  const handleAddColor = () => {
    setShowNiceMessage(true);
    setTimeout(() => {
      setCurrentColor({ r: 255, g: 255, b: 255, a: 1 }); // Reset to white
      setShowNiceMessage(false); // Hide message
    }, 2000); // Message shows for 2 seconds
  };

  const appStyle = {
    backgroundColor: `rgba(${currentColor.r}, ${currentColor.g}, ${currentColor.b}, ${currentColor.a})`,
    height: "100vh",
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: "center",
  };

  const messageStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    fontSize: '4em',
    color: 'white',
    textShadow: '2px 2px 8px rgba(0,0,0,0.5)',
    opacity: showNiceMessage ? 1 : 0,
    transition: 'opacity 0.5s ease-in-out',
    pointerEvents: 'none',  // Makes it non-interactive
    zIndex: 1000  // Ensures it is on top
  };

  return (
    <div className="App" style={appStyle}>
      <div>
        <h1>Color Palette</h1>
        <ChromePicker 
          color={currentColor}
          onChange={handleColorChange}
        />
        <button onClick={handleAddColor} style={{ marginTop: 20 }}>Add Color</button>
        {showNiceMessage && <div style={messageStyle}>NICE</div>}
      </div>
    </div>
  );
}

export default App;
